import Sidebar from '../../components/sidebar/Sidebar'
import './settings.css'
import PIC from '../../images/cloudy.JPEG'
import { useContext, useState } from 'react'
import axios from 'axios'
import { Context } from '../../context/Context'

export default function Settings() {
    const { user } = useContext(Context)
    const [file, setFile] = useState(null)
    const [username, setUsername] = useState("")
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")

    const handleSubmit = async(e) => {
        e.preventDefault();
        const updatedUser = {
            userId: user._id,
            username,
            email,
            password,
        }
        if (file) {
            const data = new FormData();
            const filename = Date.now() + file.name;
            data.append("name", filename)
            data.append("file", file)
            updatedUser.profilePic = filename
            try {
                await axios.post("/upload", data)
            } catch (err) {

            }
        }
        try {
            const res = await axios.put("/users"+user._id, updatedUser);
            window.location.replace("/post/" + res.data._id)
        } catch (err) {

        }
    }
  return (
    <div className="settings">
        <div className='settingsWrapper'>
            <div className="settingsTitle">
                <span className="settingsUpdateTitle">
                    Update Account    
                </span>    
                <span className="settingsDeleteTitle">
                    Delete Account    
                </span>    
            </div>
            <form className="settingsForm">
                <label >Profile Picture</label>
                <div className="settingsProfPic">
                    <img src={user.profilePic}  alt="" />
                    <label htmlFor="fileInput">
                    <i className="settingsProfPicIcon fa-solid fa-arrow-up-from-bracket"></i>
                    </label>
                    <input type="file" id="fileInput" style={{display:"none"}}/>
                </div>
                <label>Username</label>
                <input type="text" placeholder='pikapika' />
                <label>Email</label>
                <input type="email" placeholder='ashketchum@gmail.com'/>
                <label>Password</label>
                <input type="password"/>
                <button className="settingsSubmit">Update</button>
            </form>
        </div> 
        <Sidebar/>
    </div>
  )
}
