import './singlePost.css';
import axios from 'axios';
import { useContext, useEffect, useState } from 'react';
import { useLocation } from 'react-router';
import { Link } from 'react-router-dom';
import { Context } from '../../context/Context';

export default function SinglePost() {
    const location = useLocation()
    const path = (location.pathname.split("/")[2])
    const [post, setPost] = useState({})
    const ProfPic = "https://daily-snapshot-api.herokuapp.com/imgs/"
    const { user } = useContext(Context)
    const [title, setTitle] = useState("")
    const [desc, setDesc] = useState("")
    const [updateMode, setUpdateMode] = useState(false)

    useEffect(() => {
        const getPost = async () => {
            const res = await axios.get("https://daily-snapshot-api.herokuapp.com/api/posts/" + path);
            setPost(res.data)
            setTitle(res.data.title)
            setDesc(res.data.desc)
        }
        getPost()
    }, [path])

    const handleDelete = async () =>
    {
        try {
            await axios.delete(`https://daily-snapshot-api.herokuapp.com/api/posts/${post._id}`, {
                data: {username: user.username}})
            window.location.replace("/")
        } catch (err) {
            console.log(err)
        }
    }

    const handleUpdate = async () => {
        try {
            await axios.put(`https://daily-snapshot-api.herokuapp.com/api/posts/${post._id}`, {
                username: user.username, title, desc})
            setUpdateMode(false)
        } catch (err) {
            console.log(err)
        }
    }
    return (
        <div className='singlePost'>
            <div className="singlePostWrapper"> 
                {post.photo && <img src={ProfPic + post.photo} alt="Image" className="singlePostImg" />}{
                    updateMode ? (
                        <input type="text" value={title} className="singlePostTitleInput" autoFocus={true}
                        onChange={(e) => setTitle(e.target.value)}
                        /> ) : (
                        <h1 className="singlePostTitle">
                            {title}
                            {post.username === user?.username && (
                                <div className="singlePostEdit">
                                    <i className="singlePostIcon fa-solid fa-pen-to-square" onClick={()=>setUpdateMode(true)}></i>
                                    <i className="singlePostIcon fa-solid fa-trash-can" onClick = {handleDelete}></i>
                                </div>
                            )}
                        </h1>
                    )
                }

                <div className="singlePostInfo">
                    <span className='singlePostAuthor'>Author:
                        <Link to={`/?user=${post.username}`} className="link">
                            <b> {post.username}</b>
                        </Link>
                    </span>
                    <span className="singlePostDate"> {new Date(post.createdAt).toDateString()}</span>
                </div>
                {updateMode ? (
                    <textarea className="singlePostDescInput" value = {desc} onChange={(e) => setDesc(e.target.value)}/>
                ) : (
                    <p className='singlePostDesc'>
                        {desc}
                    </p>
                )}
                {updateMode && <button className="singlePostButton" onClick={handleUpdate}>Update</button>}
            </div>
        </div>
    )
}
